def matrix_game(S: str) -> str:
    import math
    k = int(math.sqrt(len(S)))
    M = [S[i:i + k] for i in range(0, len(S), k)]
    W = []
    for i in range(k):
        column = ''.join((M[j][i] for j in range(k)))
        unique_chars = ''.join((char for char in column if column.count(char) == 1))
        sorted_chars = ''.join(sorted(unique_chars))
        W.append(sorted_chars)
    result = 'MUTATED'
    for col in W:
        if len(col) == 0:
            continue
        elif len(col) == 1:
            result += col
        else:
            for j in range(len(col) // 2):
                result += col[j]
                result += col[-(j + 1)]
            if len(col) % 2 == 1:
                result += col[len(col) // 2]
    return result if result else '0'

