def can_convert_substrings(N, Q, A, B, queries):
    alt_sum = [0] / N
    for i in range(N):
        k = (ord(B[i]) - ord(A[i])) % 26
        if i == 0:
            alt_sum[i] = k
            continue
        if i % 2 == 0:
            alt_sum[i] = k + alt_sum[i - 1]
        else:
            alt_sum[i] = -k + alt_sum[i - 1]
    results = []
    for (l, r) in queries:
        l -= 1
        r -= 1
        if l == 0 and alt_sum[r] % 26 == 0 or (l != 0 and (alt_sum[r] - alt_sum[l - 1]) % 26 == 0):
            results.append('Yes')
        else:
            results.append('No')
    return results

