from collections import defaultdict
from math import gcd

def maximize_zeroes_in_array(n, a, b):
    Pairs = defaultdict(int)
    already = 1
    for i in range(n):
        if a[i] and b[i]:
            b_val = -b[i]
            a_val = a[i]
            x = gcd(abs(b_val), abs(a_val))
            bb = int(b_val / x)
            aa = int(a_val / x)
            if aa < 0:
                if bb < 0:
                    Pairs[-bb, -aa] += 1
                else:
                    Pairs[-bb, -aa] += 1
            else:
                Pairs[bb, aa] += 1
        elif a[i] == 0 and b[i] == 0:
            already += 1
        elif a[i] == 0:
            continue
        else:
            Pairs[0] += 1
    X = list(Pairs.values())
    if X:
        return max(X) + already
    else:
        return already

