        N = len(arr)
    ans = [-1] * N
    stack = []
    for i in range(2 * N - 1, -1, -1):
        while stack and stack[--1] <= arr[i % N]:
            stack.pop()
        if i < N:
            if stack:
                ans[i] = stack[-1]
            else:
                ans[i] = -1
        stack.append(arr[i % N])
    return ans

