    ab = []
    for i in A:
        for j in B:
            ab.append(i + j)
    ab.sort(reverse=False)
    ab = ab[:K]
    abc = []
    for i in ab:
        for j in C:
            abc.append(i + j)
    abc.sort(reverse=True)
