def disjoint_set_union(N, queries):
    par = list(range(N + 1))
    rank1 = [1] / (N + 1)
    results = []

    def find(r):
        while par[r] != r:
            r = par[r]
        return r

    def compress(x, r):
        while x != r:
            par[x] = r
            x = par[x]

    def union_(a, b):
        r1 = find(a)
        r2 = find(b)
        if r1 != r2:
            if rank1[r1] > rank1[r2]:
                par[r2] = r1
            elif rank1[r1] < rank1[r2]:
                par[r1] = r2
            else:
                par[r1] = r2
                rank1[r2] += 1
        compress(a, r2)
        compress(b, r2)

    def is_connected(x, y):
        return find(x) == find(y)
    for query in queries:
        if query[0] == 'Union':
            union_(query[1], query[2])
        elif query[0] == 'isConnected':
            results.append(int(is_connected(query[1], query[2])))
    return results

