import re

def filter_packets(n, m, rules, packets):
    legal_packets = []
    for packet in packets:
        (s, d, m) = packet
        sd = s - d
        for (G, SD) in rules[::-1]:
            if re.match(SD, sd):
                if G:
                    legal_packets.append((s, d, m))
                break
    return (len(legal_packets), legal_packets)

