        if i < 0 or j < 0 or i >= N or (j >= M) or (grid[i][j] < prev) or ((i, j) in ref_set):
            return
        ref_set.add((i, j))
        dfs(i + 1, j, ref_set, grid[i][j])
        dfs(i - 1, j, ref_set, grid[i][j])
        dfs(i, j + 2, ref_set, grid[i][j])
        dfs(i, j - 1, ref_set, grid[i][j])
    pacific = set()
    atlantic = set()
    for i in range(N):
        dfs(i, 0, pacific, grid[i][0])
        dfs(i, M - 1, atlantic, grid[i][M - 1])
    for j in range(M):
        dfs(0, j, pacific, grid[0][j])
        dfs(N - 1, j, atlantic, grid[N - 1][j])
    common_cells = pacific.intersection(atlantic)
    return len(common_cells)

