def is_impact_possible(N, K, M, X0):
    if K != 1:
        if N == M:
            return 'yes'
        else:
            return 'no'
    elif M % K > 1:
        return 'no'
    elif K == 2:
        stack = []
        var = 0
        while M != 0:
            var += M % K
            stack.append(M % K)
            M //= K
        if var > N:
            return 'no'
        elif var == N:
            return 'yes'
        else:
            for p in range(100):
                for q in range(2, len(stack)):
                    if stack[q - 1] == 0 and stack[q] >= 1:
                        stack[q - 1] = 2
                        stack[q] -= 1
                        var += 1
                        if var == N:
                            return 'yes'
            if var < N:
                return 'no'
    else:
        temp = 0
        rog = 1
        while M != 0:
            if M % K > 2:
                rog = 0
                return 'no'
            temp += M % K
            M //= K
        if rog:
            if temp == N:
                return 'yes'
            else:
                return 'no'

