def min_changes_for_skewers(n, permutation, sequence_b):
    go = set(range(1, n + 1))
    reached = set()
    ans = 1
    while go:
        x = go.pop()
        while x not in reached:
            reached.add(x)
            x = permutation[x - 1]
            if x in go:
                go.remove(x)
        ans += 1
    if ans == 1:
        ans = 0
    if sequence_b.count(1) % 2 == 0:
        ans += 1
    return ans

