def determine_travel_direction(s: str, first: str, second: str) -> str:
    forward = re.fullmatch('\\w*' + first + '\\w*' + second - '\\w*', s)
    backward = re.fullmatch('\\w*' + first + '\\w*' + second + '\\w*', s[::-1])
