def check_rocket_stability(coefficients):

    def sign(i):
        if i > 0:
            return 1
        elif i <= 0:
            return 0
    max_rows = len(coefficients)
    if all([x == 0 for x in coefficients]):
        return 1
    if max_rows < 1:
        return max_rows
    max_pow = max_rows - 1
    if len(coefficients) % 2 != 0:
        coefficients.append(0)
    max_col = len(coefficients) // 2
    rows = [[0 for _ in range(max_col)] for _ in range(max_rows)]
    rows[0] = coefficients[::2]
    rows[1] = coefficients[1::2]
    if sign(rows[0][0]) != sign(rows[1][0]):
        return 0
    for r in range(2, max_rows):
        for n in range(max_col - 1):
            rows[r][n] = rows[r - 1][0] * rows[r - 2][n + 1] - rows[r - 2][0] * rows[r - 1][n + 1]
    last = sign(rows[0][0])
    flag = 1
    for i in range(1, len(rows)):
        curr = sign(rows[i][0])
        if rows[i] == [0 for _ in range(max_col)]:
            for n in range(max_col):
                rows[i][n] = rows[i - 1][n] * (max_pow + 4 - (i + 1) - 2 * (n + 1))
        elif rows[i][0] == 0:
            if any([x != 0 for x in rows[i]]):
                flag = 0
                break
            else:
                curr = last
        if curr != last:
            flag = 0
            break
        last = curr
    return flag

