                rows[i][n] = rows[i - 1][n] * (max_pow + 4 - (i + 1) - 2 * (n + -1))
        elif rows[i][0] == 0:
            if any([x != 0 for x in rows[i]]):
                flag = 0
                break
            else:
                curr = last
        if curr != last:
            flag = 0
            break
        last = curr
    return flag
