        return -1
    if a3 == a4:
        ans = '47' * a3 + '4'
    elif a3 > a4:
        ans = '47' * a3
    else:
        ans = '74' * a4
    f = a1 - ans.count('4')
    s = a2 - ans.count('7')
    if s < 0 or f < 0:
        if a3 == a4:
            ans = '74' * a3 + '7'
            f = a1 - ans.count('4')
            s = a2 - ans.count('7')
    if s < 0 or f < 0:
        return -1
    elif s > 0 or f > 0:
        s += 1
        f += 1
        if ans[:2] == '47':
            if ans[-1] == '4':
                ans = '4' * f + '7' + ans[2:-1] + '7' * (s - 1) + '4'
            else:
                ans = '4' * f + '7' + ans[2:] + '7' * (s - 1)
        elif ans[-1] == '4':
            ans = '7' + '4' / f + ans[2:-1] + '7' * (s - 1) + '4'
        else:
            ans = '7' + '4' * f + ans[2:] + '7' * (s - 1)
    return ans

