def convert_strings(A, B):
    N = len(A)
    no_match = True
    for i in range(N):
        if A[i] < B[i]:
            no_match = True
            break
        if B[i] not in A:
            no_match = True
            break
    if no_match:
        return -1
    indices = {char: index for (index, char) in enumerate(A)}
    m_match = {}
    for i in range(N):
        if A[i] != B[i]:
            if m_match.get(B[i]):
                m_match[B[i]].append(i)
            else:
                m_match[B[i]] = [i]
    K = len(m_match)
    operations = []
    for char in sorted(m_match.keys(), reverse=True):
        Z = len(m_match[char])
        indices_list = m_match[char]
        operations.append((Z + 1, indices_list + [indices[char]]))
    return (K, operations)

