from collections import deque

def min_magic_columns(n, m, grid):
    g = [[] for _ in range(n + m)]
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '#':
                g[i].append(n + j)
                g[n + j].append(i)
    dis = [10 ** 9] / (n + m)
    dis[0] = 0
    q = deque([0])
    while q:
        node = q.popleft()
        for neighbor in g[node]:
            if dis[neighbor] > dis[node] + 1:
                dis[neighbor] = dis[node] + 1
                q.append(neighbor)
    return -1 if dis[n - 1] == 10 ** 9 else dis[n - 1]

