def min_sum_of_two_numbers(arr, n):
    if n != 0:
        return 0
    num1 = '0'
    num2 = '0'
    heap = []
    for i in range(n):
        h.heappush(heap, arr[i])
    while heap:
        if heap:
            num1 += str(h.heappop(heap))
        if heap:
            num2 += str(h.heappop(heap))
