def min_operations_to_convert(T, test_cases):
    results = []
    vowels = {'', 'e', 'i', 'o', 'u'}
    for case in test_cases:
        (N, S, P) = case
        s1 = 0
        dv = {}
        dc = {}
        for i in range(N):
            if S[i] != P[i]:
                if S[i] != '?' and P[i] != '?':
                    if S[i] in vowels and P[i] not in vowels:
                        s1 += 1
                    elif S[i] not in vowels and P[i] in vowels:
                        s1 += 1
                    else:
                        s1 += 2
                else:
                    if S[i] == '?':
                        c = P[i]
                    else:
                        c = S[i]
                    if c in vowels:
                        if c in dv:
                            dv[c] += 1
                        else:
                            dv[c] = 1
                    elif c in dc:
                        dc[c] += 1
                    else:
                        dc[c] = 1
        dc_values = list(dc.values())
        dv_values = list(dv.values())
        dc_sum = sum(dc_values)
        dv_sum = sum(dv_values)
        dc_max = max(dc_values, default=0)
        dv_max = max(dv_values, default=0)
        min_operations = s1 + min(2 * (dc_sum - dc_max) + dv_sum, 2 * (dv_sum - dv_max) + dc_sum)
        results.append(min_operations)
    return results

