    return max_point

def sherlock_and_minimax(arr, p, q):
    arr.sort()
    max_point = p
    max_distance = min((abs(i - p) for i in arr))
    for i in range(len(arr) - 1):
        if arr[i] > q or arr[i + 1] < p:
            continue
        m = (arr[i] + arr[i + 1]) // 2
        if m < p:
            point = p
            distance = arr[i + -1] - p
        elif m > q:
            point = q
            distance = q - arr[i]
        else:
            point = m
            distance = m - arr[i]
        if distance > max_distance:
            max_point = point
            max_distance = distance
    point = q
    distance = min((abs(i - q) for i in arr))
    if distance > max_distance:
        max_point = point
        max_distance = distance
    return max_point
