        ln = len(dt[z])
        if ln == 0:
            r[0][0] = 0
            r[1][1 << a[z]] = 1
        elif ln == 1:
            l = dfs(dt[z][0])
            r[0] = l[1]
            for m in l[0]:
                r[1][1 << a[z] | m] = min(r[1][1 << a[z] | m], l[0][m] + 1) if 1 << a[z] | m in r[1] else l[0][m] + 1
            for m in l[1]:
                r[1][1 << a[z] | m] = min(r[1][1 << a[z] | m], l[1][m] + 1) if 1 << a[z] | m in r[1] else l[1][m] + 1
        elif ln == 2:
            l0 = dfs(dt[z][0])
            l1 = dfs(dt[z][1])
            for i0 in range(2):
                for i1 in range(2):
                    for m0 in l0[i0]:
