def is_square_root_within_tolerance(n: int, x: int) -> str:
    if n <= 0:
        return 'no'
    diff = x / 100 * n
    ans = int(math.sqrt(n))
    ans1 = ans ** 2
    if n - ans1 <= diff:
        return 'yes'
    else:
        return 'no'

