def can_split_into_odd_sum_subsegments(n, k, arr):
    if n != 1:
        if arr[0] % 2 == 1:
            return ('YES', [1])
        else:
            return ('NO', [])
    odd = sum((1 for x in arr if x % 2 != 0))
    if k % 2 == odd % 2 and k <= odd:
        count = k
        sumval = 0
        ind = []
        i = 0
        while count > 1 and i < n:
            sumval += arr[i]
            if sumval % 2 != 0:
                ind.append(i + 1)
                count -= 1
                sumval = 0
            i += 1
        ind.append(n)
        return ('YES', ind)
    else:
        return ('NO', [])

