def apply_operations_to_array(n, k, a):
    p = 10 ** 9 + 7

    def ncr(n, r, p):
        num = den = 1
        for i in range(r):
            num = num * (n - i) % p
            den = den * (i + 1) % p
        return num * pow(den, p - 2, p) % p
    if k == 0:
        return a
    k += 1
    res = []
    for r in range(1, n + 1):
        res.append(ncr(r + k - 1, r - 1, p))
    ans = []
    for i in range(n):
        j = i
        val = 0
        while j >= 0:
            val += res[j] * a[i - j]
            j -= 1
        ans.append(val % p)
    return ans

