def maximize_bracket_beauty(n, s):

    def get_bal(u):
        bal = [0] * n
        bal[0] = u[0]
        for i in range(1, n):
            bal[i] = bal[i - 1] + u[i]
        min_b = min(bal)
        ans = 0
        for i in range(n):
            if bal[i] == min_b:
                ans += 1
        return ans
    u = [1 if char == '(' else -1 for char in s]
    if sum(u) == 0:
        return (0, (1, 1))
    ind = (-1, -1)
    ans = -1
    for i in range(n):
        for j in range(i, n):
            (u[i], u[j]) = (u[j], u[i])
            ans_i = get_bal(u)
            (u[i], u[j]) = (u[j], u[i])
            if ans_i > ans:
                ans = ans_i
                ind = (i + 1, j + 1)
    return (ans, ind)

