def calculate_min_fall_damage(n, m, heights, quests):
    right = [0] / n
    left = [0] * n
    x = 0
    for i in range(1, n):
        if heights[i - 1] > heights[i]:
            x += heights[i - 1] - heights[i]
        right[i] = x
    x = 0
    for i in range(n - 2, -1, -1):
        if heights[i + 1] > heights[i]:
            x += heights[i + 1] - heights[i]
        left[i] = x
    results = []
    for (p, q) in quests:
        if p < q:
            results.append(right[q - 1] - right[p - 1])
        else:
            results.append(left[q - 1] - left[p - 1])
    return results

