def process_queries(n, edges, queries):
    from collections import defaultdict
    mapp = defaultdict(set)
    for (a, b) in edges:
        mapp[a].add(b)
        mapp[b].add(a)
    parentmapp = defaultdict(lambda : -1)
    needed = defaultdict(lambda : 0)

    def remove_parent(i, p=None):
        if p is not None:
            parentmapp[i] = p
            needed[p] += 1
            mapp[i].remove(p)
        for j in mapp[i]:
            remove_parent(j, i)
    remove_parent(1)
    dp = [0] / (n + 1)
    dp[0] = -1
    ans = n - 1
    results = []
    for v in queries:
        if v[0] == 1:
            u = v[1]
            zc = needed[u]
            if zc == 0 and dp[u] == 0:
                dp[u] = 1
                if u != 1:
                    needed[parentmapp[u]] -= 1
                    ans -= 1
                else:
                    ans = n - 1
        elif v[0] == 2:
            results.append(ans)
    return results

