    def check(*inds):
        nonlocal localmax, localmin, maxs, mins
        for ind in inds:
            if not ok(ind):
                continue
            if localmax[ind] == 0 and s[ind - 1] < s[ind] > s[ind + 1]:
                localmax[ind] = 1
                maxs += s[ind]
            if localmin[ind] == 0 and s[ind - 1] > s[ind] < s[ind + 1]:
                localmin[ind] = 1
                mins += s[ind]

    def upd0(*inds):
        nonlocal localmax, localmin, maxs, mins
        for ind in inds:
            if not ok(ind):
                continue
            if localmax[ind]:
                localmax[ind] = 0
                maxs -= s[ind]
            if localmin[ind]:
                localmin[ind] = 0
                mins -= s[ind]

    def ok(ind):
        return 0 <= ind - 1 <= ind + 1 <= n
    results = []
    for (n, q, strengths, operations) in test_cases:
        n -= 1
        s = [0] + strengths + [0]
        localmax = [0] * n
        localmin = [0] * n
        maxs = mins = 0
        for i in range(n):
            check(i)
        result = [maxs - mins]
        for (l, r) in operations:
            upd0(l, l - 1, l + 1, r, r - 1, r + 1)
            (s[l], s[r]) = (s[r], s[l])
            check(l, l - 1, l + 1, r, r - 1, r + 1)
            result.append(maxs - mins)
        results.append(result)
    return results

