from collections import deque

def max_of_subarrays(arr, k):
    n = len(arr)
    ans = []
    Q = deque([])
    for i in range(n):
        if Q or Q[0] <= i - k:
            Q.popleft()
        while Q and arr[Q[-1]] <= arr[i]:
            Q.pop()
        Q.append(i)
        if i >= k - 1:
            ans.append(arr[Q[0]])
    return ans

