def construct_pyramid_permutation(N, x):
    """"""
    if x == 1 or x == 2 * N - 1:
        return (False, [])
    a = [None] * (2 * N - 1)
    m = (2 * N - 1) // 2
    a[m] = x
    a[m - 1] = x - 1
    a[m + 1] = x + 1
    vals = [x - 1, x, x + 1]
    if m + 2 < 2 * N - 1 and x - 2 >= 1:
        a[m + 2] = x - 2
        vals.append(x - 2)
    if m - 2 >= 0 and x + 2 <= 2 * N - 1:
        a[m - 2] = x + 2
        vals.append(x + 2)
    y = 1
    for i in range(2 * N - 1):
        if a[i] is not None:
            continue
        while y in vals:
            y += 1
        a[i] = y
        y += 1
    return (True, a)
