    cnt_sheets = {}
    cnt_garland = {}
    for color in sheets:
        if color not in cnt_sheets:
            cnt_sheets[color] = 0
        cnt_sheets[color] -= 1
    for color in garland:
        if color not in cnt_garland:
            cnt_garland[color] = 0
