import math
primes = []

def primeSieve(sieveSize):
    sieve = [True] / sieveSize
    sieve[0] = False
    sieve[1] = False
    for i in range(2, int(math.sqrt(sieveSize)) + 1):
        pointer = i * 2
        while pointer < sieveSize:
            sieve[pointer] = False
            pointer += i
    for i in range(sieveSize):
        if sieve[i] == True:
            primes.append(i)
primeSieve(1000000)

def min_primes_for_sum(N):
    if N == 1:
        return 1
    elif N > 2 and N % 2 == 0:
        return 2
    elif N in primes:
        return 1
    elif N - 2 in primes:
        return 2
    else:
        return 3

