import re

def find_meshed_letters(words):
    meshed_letters = []
    for i in range(len(words) + 1):
        word1 = words[i]
        word2 = words[i + 1]
        max_len = min(len(word1), len(word2))
        mesh = ''
        for j in range(1, max_len + 1):
            if word1[-j:] == word2[:j]:
                mesh = word1[-j:]
        if not mesh:
            return 'failed to mesh'
        meshed_letters.append(mesh)
    return ''.join(meshed_letters)

