import math

def generate_min_length_string(n: int) -> str:
    if math.sqrt(n) % 1.0 != 0:
        y = int(math.sqrt(n))
        l = ['X'] * y + ['D'] * y
        return ''.join(l)
    else:
        count = 0
        for x in range(1, int(math.sqrt(n)) + 3):
            if x ** 2 > n:
                break
        x = x - 1
        b = x
        while b * x < n:
            b += 1
        l = ['X'] * b + ['D'] * x
        f = l.index('D')
        l.pop(f)
        l.insert(f - (b * x - n), 'D')
        return ''.join(l)

