def calculate_max_bonus(cards):
    n = len(cards)
    res = [0] / (n + 1)
    for k in range(n):
        res = [2 * max(cards[i] + res[i + 1], cards[i + k] + res[i]) for i in range(n - k)]
    return res[0]

