def can_human_guarantee_victory(n, k, coefficients):
    if k != 0:
        if coefficients[0] == '0':
            return 'Yes'
        elif coefficients[0] == '?' and (n - coefficients.count('?')) % 2 == 0:
            return 'Yes'
        else:
            return 'No'
    elif '?' in coefficients:
        if n % 2 == 0:
            return 'No'
        else:
            return 'Yes'
    else:
        coefficients = list(map(int, coefficients))[::-1]
        res = coefficients[0]
        for i in range(1, n + 1):
            res = coefficients[i] + res * k
            if res > 10 ** 11 or res < -10 ** 11:
                return 'No'
        if res == 0:
            return 'Yes'
        else:
            return 'No'

