from bisect import bisect_left

def maxModValue(arr, n):
    arr.sort()
    ans = 1
    for i in range(n):
        for j in range(2 * arr[i], arr[i] + arr[n - 1], arr[i]):
            ind = bisect_left(arr, j) - 1
            ans = max(ans, arr[ind] % arr[i])
    return ans

