from math import ceil

def generate_union_jack(size):
    if not isinstance(size, (int, float)):
        return False
    size = max(7, ceil(size))
    half_size = (size - 1) / 2
    is_odd = size % 2 != 0
    flag = []
    for j in range(size):
        row = []
        for i in range(size):
            if len({i, size - j - 1, j, half_size, half_size + is_odd}) < 4 + is_odd:
                row.append('X')
            else:
                row.append('-')
        flag.append(''.join(row))
    return '\n'.join(flag)

