def calculate_max_operations(n, s):
    balls = list(s)
    balls.reverse()
    stack = []
    c = 1
    pred = balls[0]
    for b in balls:
        if b == pred:
            c += 1
        else:
            stack.append([pred, c])
            c = 1
        pred = b
    else:
        stack.append([pred, c])
    cnt = 0
    l = len(balls)
    while True:
        if stack[-1][0] == 'R':
            l -= stack[-1][1]
            stack.pop()
        else:
            k = stack[-1][1]
            cnt += 2 ** k - 1
            stack.pop()
            if stack:
                stack[-1][1] += k
            else:
                stack.append(['R', k])
            continue
        if not stack:
            break
        stack[-1][1] -= 1
        if stack[-1][1] == 0:
            stack.pop()
        if not stack:
            stack.append(['R', 1])
        elif stack[-1][0] == 'R':
            stack[-1][1] += 1
        else:
            stack.append(['R', 1])
        if l < n:
            stack.append(['B', n - l])
        l = n
        cnt += 1
    return cnt

