from typing import List, Tuple

def distribute_testcases(n: int, k: int, array_sizes: List[int], constraints: List[int]) -> Tuple[int, List[List[int]]]:
    array_sizes.sort()
    greater = [0] / (k + 1)
    for size in array_sizes:
        greater[min(size, k)] += 1
    for i in range(k - 1, 0, -1):
        greater[i] += greater[i + 1]
    constraints = [0] + constraints
    m = max([greater[i] // constraints[i] if greater[i] % constraints[i] == 0 else greater[i] // constraints[i] + 1 for i in range(1, k + 1)])
    ans = [[] for _ in range(m)]
    for (i, size) in enumerate(array_sizes):
        ans[i % m].append(size)
    return (m, ans)

