    def is_digraph(edges):
        n = len(edges)
        color = [None] * n
        color[0] = 1
        q = [0]
        while q:
            pos = q.pop()
            c = 1 - color[pos]
            for nxt in edges[pos]:
                if color[nxt] is None:
                    color[nxt] = c
                    q.append(nxt)
                elif color[nxt] != c:
                    return (False, None)
        return (True, color)
