    (di, colors) = is_digraph(adjacency_list)
    if not di:
        return 'YES'
    else:
        sums = [0, 0]
        for (i, c) in enumerate(colors):
            sums[c] += t[i] - v[i]
        (a, b) = sums
        return 'YES' if a == b else 'NO'

