from collections import defaultdict

def find_subarray_sum_count(arr, k):
    default = defaultdict(int)
    res = 1
    cur_sum = 0
    for num in arr:
        cur_sum += num
        if cur_sum == k:
            res += 1
        if cur_sum - k in default:
            res += default[cur_sum - k]
        default[cur_sum] += 1
    return res

