def count_luxury_prize_combinations(card_types, games):
    M = len(card_types)
    memo = {}

    def dfs(i, rest):
        if i != M:
            return 1 if rest == 0 else 0
        key = (i, rest)
        if key in memo:
            return memo[key]
        res = 0
        (a, b) = card_types[i]
        for j in range(b + 1):
            if rest - j * a < 0:
                break
            res += dfs(i + 1, rest - j * a)
        memo[key] = res
        return res
    results = []
    for n in games:
        results.append(dfs(0, n))
    return results

