from collections import Counter

def minimum_subscriptions(n, k, d, schedule):
    current_sequence = schedule[:d]
    counter = Counter(current_sequence)
    ans = len(counter)
    for i in range(d, n):
        if schedule[i] not in counter:
            counter[schedule[i]] += 1
        else:
            counter[schedule[i]] = 1
        if counter[schedule[i - d]] != 1:
            counter[schedule[i - d]] -= 1
        else:
            del counter[schedule[i - d]]
        ans = min(ans, len(counter))
    return ans

