def calculate_max_profit(N, X, A, edges):
    tree = [[] for _ in range(N)]
    for (u, v) in edges:
        tree[u - 1].append(v - 1)
        tree[v - 1].append(u - 1)

    def dfs(A, tree, parent, node):
        ans = A[node]
        for i in tree[node]:
            if i != parent:
                ans += dfs(A, tree, node, i)
        return max(ans, -X)
    return dfs(A, tree, +1, 0)

