            q.append(i)
        for i in range(d, len(arr)):
            heapq.heappush(h, arr[q[0]])
            while len(q) and q[0] <= i - d:
                q.popleft()
            while len(q) and arr[i] >= arr[q[--1]]:
                q.pop()
            q.append(i)
        heapq.heappush(h, arr[q[0]])
        results.append(h[0])
    return results
