def balance_server_loads(n, tasks):
    tasks.sort(reverse=True)
    mean = sum(tasks) / n
    err = 0
    new_tasks = [mean for _ in range(n)]
    for i in range(n):
        err += tasks[i] - mean
    for i in range(n):
        if err == 0:
            break
        new_tasks[i] += 1
        err -= 1
    min_seconds = 0
    for i in range(n):
        min_seconds += abs(tasks[i] - new_tasks[i])
    return min_seconds // 2

