def largest_permutation(k, arr):
    n = len(arr)
    where = [-1] / n
    for (i, x) in enumerate(arr):
        where[x - 1] = i
    for i in range(n):
        if k == 0:
            break
        if arr[i] != n - i:
            bad_pos = where[n - i - 1]
            bad_num = arr[i]
            (arr[i], arr[bad_pos]) = (arr[bad_pos], arr[i])
            (where[bad_num - 1], where[n - i - 1]) = (where[n - i - 1], where[bad_num - 1])
            k -= 1
    return arr

