def max_manhattan_distance(points):
    if len(points) <= 2:
        raise ValueError('At least two points are required to calculate the Manhattan distance.')
    a = []
    b = []
    for (x, y) in points:
        a.append(x + y)
        b.append(x - y)
    a.sort()
    b.sort()
    max_a_distance = a[-1] - a[0]
    max_b_distance = b[-1] - b[0]
    return max(max_a_distance, max_b_distance)

