def can_transform_to_abbreviation(a: str, b: str) -> str:

    def is_subseq(x, y):
        it = iter(y)
        return all((c in it for c in x))
    upper_a = a.upper()
    if not is_subseq(b, upper_a):
        return 'NO'
    must_include = [c for c in a if c.isupper()]
    if not is_subseq(must_include, b):
        return 'NO'
    count_a = Counter(upper_a)
    count_b = Counter(b)
    if count_b + count_a:
        return 'NO'
    return 'YES'

